import styled from 'styled-components';

export const BrandSectionWrapper = styled.div`
  display: flex;
  padding: 20px;
  align-items: center;
  &:first-child {
    border-top: none;
  }
  .vCenter {
    flex: 1;
    padding-left: 10px;
    .icInfo {
      font-size: 50px;
    }
    .txtTitle {
      font-size: 16px;
      font-weight: 600;
      padding-top: 20px;
      color: ${({ theme }) => theme.text.title};
    }
    .txtValue {
      font-size: 16px;
      font-weight: bold;
    }
  }
  .vRight {
    font-size: 20px;
  }

  @media (max-width: 577px) {
  }
`;
