import styled from 'styled-components';

export const BrandItemWrapper = styled.div`
  display: flex;
  padding: 20px;
  border-radius: 15px;
  margin: 15px;
  width: 250px;
  min-width: 250px;
  background: ${({ theme }) => theme.background.container};
  align-items: center;
  .vCenter {
    flex: 1;
    padding-left: 10px;
    .icInfo {
      font-size: 50px;
    }
    .txtTitle {
      font-size: 16px;
      padding-top: 20px;
      color: ${({ theme }) => theme.text.headingLight};
    }
    .txtValue {
      font-size: 16px;
      font-weight: bold;
      color: ${({ theme }) => theme.text.title};
    }
  }
  .vRight {
    font-size: 20px;
  }

  @media (max-width: 577px) {
  }
`;
