const Categories = props => {
  const settings = {
    swipeToSlide: true,
    infinite: false,
    // centerMode: true,
    draggable: true,
    // centerPadding: '60px',
    responsive: [
      {
        breakpoint: 768,
        settings: {
          centerMode: true,
          dots: false,
          centerPadding: '20px',
        },
      },
    ],
  };
  return (
    <CategoriesWrapper>
      <Carousel {...settings}>
        {chunk(CATEGORIES, 9).map((group, index) => (
          <div className="categoryGroup">
            <Card className='noPadding' bordered={false} key={String(`group${index}`)}>
              <Row type="flex">
                {group.map((e, index2) => (
                  <Col key={String(`category${index2}`)} span={8}>
                    <CategoryItem item={e} />
                  </Col>
                ))}
              </Row>
            </Card>
          </div>
        ))}
      </Carousel>
    </CategoriesWrapper>
  );
};

Categories.propTypes = {};

export default Categories;
