import React from 'react';
import PropTypes from 'prop-types';
import { Icon } from 'antd';
import { CouponItemWrapper } from './styles';

const CouponItem = ({ item, isShowArrow }) => (
  <CouponItemWrapper>
    <div className="vCenter">
      <Icon className="icInfo" type={item.icon} />
      <div className="txtTitle">
        <span className="txtValue">{item.value}</span> 
        {item.title}
      </div>

    </div>
    {isShowArrow && <Icon className="vRight" type="right" />}
  </CouponItemWrapper>
);
CouponItem.propTypes = {
  item: PropTypes.object,
  isShowArrow: PropTypes.bool,
};

CouponItem.defaultProps = {
  isShowArrow: true,
};

export default CouponItem;
